import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import { Button } from 'antd';
import { CheckCircleOutlined } from '@ant-design/icons';
import { Col, Row } from 'antd';

const AddBookForm = () => {
  const [borrower, setBorrower] = useState('');
  const [book, setBook] = useState('');
  const [id, setId] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [borrowedBooks, setBorrowedBooks] = useState([]);
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    fetchBorrowedBooks();
    fetchLogs();
  }, []);

  const fetchBorrowedBooks = () => {
    axios.get('http://localhost:3001/books')
      .then(response => {
        setBorrowedBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const fetchLogs = () => {
    axios.get('http://localhost:3001/logs')
      .then(response => {
        setLogs(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleAddBook = () => {
    axios.post('http://localhost:3001/books', { borrower, mobile_number: mobileNumber, book_title: book, due_date: dueDate })
      .then(response => {
        fetchBorrowedBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleDeleteBook = (bookId) => {
    axios.delete(`http://localhost:3001/books/${bookId}`)
      .then(response => {
        fetchBorrowedBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  return (
    <div className="homepage-container">
      <div className="library-container">
        <h1>Library Management System</h1>
        <div className="add-book-section">
          {/* Your input fields for adding a book */}
        </div>
        <div className="borrowed-books-section">
          <h2>Borrowed Books</h2>
          <div className="book-cards-container">
            {/* Render borrowed books */}
          </div>
        </div>
        <div className="logs-section">
          <h2>Logs</h2>
          <div className="logs-container">
            {logs.map(log => (
              <div key={log.id} className="log-card">
                <div>
                  <span className="log-info">Person: {log.person}</span>
                </div>
                <div>
                  <span className="log-info">Time: {new Date(log.entry_time).toLocaleString()}</span>
                  {log.exit_time
                    ? <span className="log-info"> - {new Date(log.exit_time).toLocaleString()}</span>
                    : <span className="log-info"> - Still in Library</span>
                  }
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddBookForm;
