import React, { useState } from 'react';
import axios from 'axios';

const AddBookForm = () => {
  const [borrower, setBorrower] = useState('');
  const [book, setBook] = useState('');

  const handleAddBook = () => {
    axios.post('http://localhost:3001/add-book', { borrower, book })
      .then(response => {
        console.log(response.data);
        // Update the UI with the new entry
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleDeleteBook = (id) => {
    axios.delete(`http://localhost:3001/delete-book/${id}`)
      .then(response => {
        console.log(response.data);
        // Update the UI to remove the deleted entry
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <input type="text" value={borrower} onChange={(e) => setBorrower(e.target.value)} />
      <input type="text" value={book} onChange={(e) => setBook(e.target.value)} />
      <button onClick={handleAddBook}>Add Book</button>
      <button onClick={() => handleDeleteBook(id)}>Delete Book</button> {/* Replace id with the actual book ID */}
    </div>
  );
};

export default AddBookForm;
