// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [books, setBooks] = useState([]);
    const [borrowerName, setBorrowerName] = useState('');
    const [bookTitle, setBookTitle] = useState('');

    useEffect(() => {
        axios.get('http://localhost:3001/books')
            .then((res) => setBooks(res.data))
            .catch((err) => console.log(err));
    }, []);

    const addBook = () => {
        if (!borrowerName || !bookTitle) {
            alert("Please enter both Borrower Name and Book Title.");
            return;
        }

        axios.post('http://localhost:3001/books', { borrower_name: borrowerName, book_title: bookTitle })
            .then(() => {
                setBooks([...books, { borrower_name: borrowerName, book_title: bookTitle }]);
                setBorrowerName('');
                setBookTitle('');
            })
            .catch((err) => console.log(err));
    };

    const deleteBook = (id) => {
        axios.delete(`http://localhost:3001/books/${id}`)
            .then(() => setBooks(books.filter((book) => book.id !== id)))
            .catch((err) => console.log(err));
    };

    return (
        <div>
            <input type="text" placeholder="Borrower Name" value={borrowerName} onChange={(e) => setBorrowerName(e.target.value)} />
            <input type="text" placeholder="Book Title" value={bookTitle} onChange={(e) => setBookTitle(e.target.value)} />
            <button onClick={addBook}>Add Book</button>
            
            {books.map((book) => (
                <div key={book.id}>
                    <p>{book.borrower_name} - {book.book_title}</p>
                    <button onClick={() => deleteBook(book.id)}>Delete</button>
                </div>
            ))}
        </div>
    );
}

export default App;
